import java.util.Scanner;

public class ej5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 5;

		for(int i = 1; i<=num; i++){
	       
	        for(int b = 1; b<=num-i; b++){
	            System.out.print(" ");
	        }

	        
	    for(int j=1; j<=(i*2)-1; j++){
	        System.out.print("*");
	    }
	    System.out.println("");
			
			
		}
        }
		

}

